        public async Task Send2Excel()
        {
			IEnumerable<TODO_Error_IncompleteImport> lst = null;
            using (var ctx = new TODO_Error_IncompleteImportRepository())
            {
                lst = await ctx.GetTODO_Error_IncompleteImportByExpressionNav(vloader.FilterExpression, vloader.NavigationExpression).ConfigureAwait(continueOnCapturedContext: false);
            }
             if (lst == null || !lst.Any()) 
              {
                   MessageBox.Show("No Data to Send to Excel");
                   return;
               }
            var s = new ExportToCSV<TODO_Error_IncompleteImportExcelLine, List<TODO_Error_IncompleteImportExcelLine>>
            {
                dataToPrint = lst.Select(x => new TODO_Error_IncompleteImportExcelLine
                {
 
                    ImportComplete = x.ImportComplete ,
                    
 
                    Type = x.Type ,
                    
 
                    ExpectedLines = x.ExpectedLines ,
                    
 
                    ImportedLines = x.ImportedLines ,
                    
 
                    CNumber = x.CNumber ,
                    
 
                    RegistrationDate = x.RegistrationDate ,
                    
 
                    ReferenceNumber = x.ReferenceNumber ,
                    
 
                    DocumentType = x.DocumentType 
                    
                }).ToList()
            };
            using (var sta = new StaTaskScheduler(numberOfThreads: 1))
            {
               await Task.Factory.StartNew(s.GenerateReport, CancellationToken.None, TaskCreationOptions.None, sta).ConfigureAwait(false);
            }
        }

