		public async Task<int> CountNav(string exp, Dictionary<string, string> navExp)
        {
            try
            {
                if (string.IsNullOrEmpty(exp) || exp == "None") return 0;
                using (var dbContext = new AllocationDSContext(){StartTracking = StartTracking})
                {
                    dbContext.Database.CommandTimeout = 0;
                    if (exp == "All" && navExp.Count == 0)
                    {
                        return dbContext.LumpedItems
										.AsNoTracking()
                                        .Count();
                    }
                    foreach (var itm in navExp)
                    {
                        switch (itm.Key)
                        {
                            case "InventoryItem":
                                return await CountWhere<InventoryItem>(dbContext, exp, itm.Value, "LumpedItem", "SelectMany")
											.ConfigureAwait(continueOnCapturedContext: false);
						}
                    }
                    return dbContext.LumpedItems.Where(exp == "All" || exp == null ? "InventoryItemId != null" : exp)
											.AsNoTracking()
                                            .Count();
                }
                
            }
            catch (Exception updateEx)
            {
                System.Diagnostics.Debugger.Break();
                //throw new FaultException(updateEx.Message);
                var fault = new ValidationFault
                {
                    Result = false,
                    Message = updateEx.Message,
                    Description = updateEx.StackTrace
                };
                throw new FaultException<ValidationFault>(fault);
            }
        }

		private static async Task<int> CountWhere<T>(AllocationDSContext dbContext, string exp, string navExp, string navProp, string rel) where T : class
        {
              switch (rel)
		    {
		        case "SelectMany":
                    return await CountWhereSelectMany<T>(dbContext, exp, navExp, navProp)
									.ConfigureAwait(continueOnCapturedContext: false);
		            
                default:
                    return await CountWhereSelect<T>(dbContext, exp, navExp, navProp)
									.ConfigureAwait(continueOnCapturedContext: false);
		    }
        }

		private static Task<int> CountWhereSelectMany<T>(AllocationDSContext dbContext, string exp, string navExp, string navProp) where T : class
        {
			try
			{
            return Task.FromResult(dbContext.Set<T>()
                .AsNoTracking()
                .Where(navExp)
                .SelectMany(navProp).OfType<LumpedItem>()
                .Where(exp == "All" || exp == null ? "InventoryItemId != null" : exp)
                .Distinct()
                .OrderBy("InventoryItemId")
                .Count());
			}
			catch (Exception)
			{
				Debugger.Break();
				throw;
			}
        }

		private static Task<int> CountWhereSelect<T>(AllocationDSContext dbContext, string exp, string navExp, string navProp) where T : class
        {
			try
			{
            return Task.FromResult(dbContext.Set<T>()
                .AsNoTracking()
                .Where(navExp)
                .Select(navProp).OfType<LumpedItem>()
                .Where(exp == "All" || exp == null ? "InventoryItemId != null" : exp)
                .Distinct()
                .OrderBy("InventoryItemId")
                .Count());
			}
			catch (Exception)
			{
				Debugger.Break();
				throw;
			}
        }

