		public decimal SumField(string whereExp, string field)
         {
             try
             {
                 using (var dbContext = new AllocationQSContext(){StartTracking = StartTracking})
                 {
                    dbContext.Database.CommandTimeout = 0;
					decimal res = 0;
                     if (string.IsNullOrEmpty(whereExp) || whereExp == "None") return 0;
                     if (whereExp == "All")
                     {
                          res = Convert.ToDecimal(dbContext.AdjustmentShortAllocations.AsNoTracking().Sum(field));
                     }
                     else
                     {
                         res = Convert.ToDecimal(dbContext.AdjustmentShortAllocations.AsNoTracking().Where(whereExp).Sum(field));
                     }
                     
                     return res;
                 }
             }
             catch (Exception updateEx)
             {
                System.Diagnostics.Debugger.Break();
                //throw new FaultException(updateEx.Message);
                    var fault = new ValidationFault
                                {
                                    Result = false,
                                    Message = updateEx.Message,
                                    Description = updateEx.StackTrace
                                };
                    throw new FaultException<ValidationFault>(fault);
             }
         }

        public Task<decimal> SumNav( string exp, Dictionary<string, string> navExp, string field)
        {
            try
            {
                if (string.IsNullOrEmpty(exp) || exp == "None") return Task.FromResult<decimal>(0);
                using (var dbContext = new AllocationQSContext(){StartTracking = StartTracking})
                {
                    dbContext.Database.CommandTimeout = 0;
                    if (!dbContext.AdjustmentShortAllocations.Any()) return Task.FromResult<decimal>(0);
                    if (exp == "All" && navExp.Count == 0)
                    {
                        return Task.FromResult(Convert.ToDecimal(dbContext.AdjustmentShortAllocations
                                                                     .AsNoTracking()
                                                                     .Sum(field)??0));
                    }
                    return Task.FromResult(Convert.ToDecimal(dbContext.AdjustmentShortAllocations.Where(exp == "All" || exp == null ? "AllocationId != null" : exp)
                                                                 .AsNoTracking()
                                                                 .Sum(field)??0));
                }
                
            }
            catch (Exception updateEx)
            {
                System.Diagnostics.Debugger.Break();
                //throw new FaultException(updateEx.Message);
                var fault = new ValidationFault
                {
                    Result = false,
                    Message = updateEx.Message,
                    Description = updateEx.StackTrace
                };
                throw new FaultException<ValidationFault>(fault);
            }
        }

