        public async Task<decimal> SumNav( string exp, Dictionary<string, string> navExp, string field)
        {
            try
            {
                if (string.IsNullOrEmpty(exp) || exp == "None") return 0;
                using (var dbContext = new CoreEntitiesContext(){StartTracking = StartTracking})
                {
                    dbContext.Database.CommandTimeout = 0;
                    if (!dbContext.xcuda_Supplementary_unit.Any()) return 0;
                    if (exp == "All" && navExp.Count == 0)
                    {
                        return Convert.ToDecimal(dbContext.xcuda_Supplementary_unit
										.AsNoTracking()
                                        .Sum(field)??0);
                    }
                    foreach (var itm in navExp)
                    {
                        switch (itm.Key)
                        {
                            case "AsycudaDocumentItem":
                                return await SumWhere<AsycudaDocumentItem>(dbContext, exp, itm.Value, "xcuda_Supplementary_unit", field, "SelectMany")
											.ConfigureAwait(continueOnCapturedContext: false);
						}
                    }
                    return Convert.ToDecimal(dbContext.xcuda_Supplementary_unit.Where(exp == "All" || exp == null ? "Supplementary_unit_Id != null" : exp)
											.AsNoTracking()
                                            .Sum(field)??0);
                }
                
            }
            catch (Exception updateEx)
            {
                System.Diagnostics.Debugger.Break();
                //throw new FaultException(updateEx.Message);
                var fault = new ValidationFault
                {
                    Result = false,
                    Message = updateEx.Message,
                    Description = updateEx.StackTrace
                };
                throw new FaultException<ValidationFault>(fault);
            }
        }

