                            case "Start":
                                return await CountWhere<Start>(dbContext, exp, itm.Value, "Parts", "Select")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "Lines":
                                return await CountWhere<Lines>(dbContext, exp, itm.Value, "Parts", "Select")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "RecuringPart":
                                return await CountWhere<RecuringPart>(dbContext, exp, itm.Value, "Parts", "SelectMany")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "ChildParts":
                                return await CountWhere<ChildParts>(dbContext, exp, itm.Value, "ChildPart", "Select")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "ParentParts":
                                return await CountWhere<ChildParts>(dbContext, exp, itm.Value, "ChildPart", "Select")
											.ConfigureAwait(continueOnCapturedContext: false);
						}
                    }
                    return dbContext.Parts.Where(exp == "All" || exp == null ? "Id != null" : exp)
											.AsNoTracking()
                                            .Count();
                }
                
            }
            catch (Exception updateEx)
            {
                System.Diagnostics.Debugger.Break();
                //throw new FaultException(updateEx.Message);
                var fault = new ValidationFault
                {
                    Result = false,
                    Message = updateEx.Message,
                    Description = updateEx.StackTrace
                };
                throw new FaultException<ValidationFault>(fault);
            }
        }

		private static async Task<int> CountWhere<T>(OCRContext dbContext, string exp, string navExp, string navProp, string rel) where T : class
        {
              switch (rel)
		    {
		        case "SelectMany":
                    return await CountWhereSelectMany<T>(dbContext, exp, navExp, navProp)
									.ConfigureAwait(continueOnCapturedContext: false);
		            
                default:
                    return await CountWhereSelect<T>(dbContext, exp, navExp, navProp)
									.ConfigureAwait(continueOnCapturedContext: false);
		    }
        }

		private static async Task<int> CountWhereSelectMany<T>(OCRContext dbContext, string exp, string navExp, string navProp) where T : class
        {
			try
			{
            return dbContext.Set<T>()
				.AsNoTracking()
                .Where(navExp)
                .SelectMany(navProp).OfType<Parts>()
                .Where(exp == "All" || exp == null ? "Id != null" : exp)
                .Distinct()
                .OrderBy("Id")
                .Count();
			}
			catch (Exception)
			{
				Debugger.Break();
				throw;
			}
        }

		private static async Task<int> CountWhereSelect<T>(OCRContext dbContext, string exp, string navExp, string navProp) where T : class
        {
			try
			{
            return dbContext.Set<T>()
				.AsNoTracking()
                .Where(navExp)
                .Select(navProp).OfType<Parts>()
                .Where(exp == "All" || exp == null ? "Id != null" : exp)
                .Distinct()
                .OrderBy("Id")
                .Count();
			}
			catch (Exception)
			{
				Debugger.Break();
				throw;
			}
        }

		  public async Task<IEnumerable<Parts>> LoadRangeNav(int startIndex, int count, string exp,
                                                                                 Dictionary<string, string> navExp, IEnumerable<string> includeLst = null)
        {
            try
            {
                using (var dbContext = new OCRContext(){StartTracking = StartTracking})
                {
                    dbContext.Database.CommandTimeout = 0;
                    if ((string.IsNullOrEmpty(exp) && navExp.Count == 0) || exp == "None") return new List<Parts>();
                    var set = AddIncludes(includeLst, dbContext);

                    if (exp == "All" && navExp.Count == 0)
                    {
                       
                        return set
									.AsNoTracking()
                                    .OrderBy(y => y.Id)
 
                                    .Skip(startIndex)
                                    .Take(count)
									.ToList();
                    }
                    foreach (var itm in navExp)
                    {
                        switch (itm.Key)
                        {
                            case "End":
                                return
                                    await
                                        LoadRangeWhere<End>(startIndex, count, dbContext, exp, itm.Value, "Parts", "Select")
													.ConfigureAwait(continueOnCapturedContext: false);

                            case "Templates":
                                return
                                    await
                                        LoadRangeWhere<Templates>(startIndex, count, dbContext, exp, itm.Value, "Parts", "SelectMany")
													.ConfigureAwait(continueOnCapturedContext: false);

                            case "PartTypes":
                                return
                                    await
                                        LoadRangeWhere<PartTypes>(startIndex, count, dbContext, exp, itm.Value, "Parts", "SelectMany")
													.ConfigureAwait(continueOnCapturedContext: false);

                            case "Start":
                                return
                                    await
                                        LoadRangeWhere<Start>(startIndex, count, dbContext, exp, itm.Value, "Parts", "Select")
													.ConfigureAwait(continueOnCapturedContext: false);

                            case "Lines":
                                return
                                    await
                                        LoadRangeWhere<Lines>(startIndex, count, dbContext, exp, itm.Value, "Parts", "Select")
													.ConfigureAwait(continueOnCapturedContext: false);

                            case "RecuringPart":
                                return
                                    await
                                        LoadRangeWhere<RecuringPart>(startIndex, count, dbContext, exp, itm.Value, "Parts", "SelectMany")
													.ConfigureAwait(continueOnCapturedContext: false);

                            case "ChildParts":
                                return
                                    await
                                        LoadRangeWhere<ChildParts>(startIndex, count, dbContext, exp, itm.Value, "ChildPart", "Select")
													.ConfigureAwait(continueOnCapturedContext: false);

                            case "ParentParts":
                                return
                                    await
                                        LoadRangeWhere<ChildParts>(startIndex, count, dbContext, exp, itm.Value, "ChildPart", "Select")
													.ConfigureAwait(continueOnCapturedContext: false);

                          
							default:
                                throw new ArgumentException("No Navigation property found for " + itm.Key);
						}

                    }
                    return set//dbContext.Parts
								.AsNoTracking()
                                .Where(exp == "All" || exp == null ? "Id != null" : exp)
								.OrderBy(y => y.Id)
 
                                .Skip(startIndex)
                                .Take(count)
								.ToList();


                }
            }
            catch (Exception updateEx)
            {
                System.Diagnostics.Debugger.Break();
                //throw new FaultException(updateEx.Message);
                    var fault = new ValidationFault
                                {
                                    Result = false,
                                    Message = updateEx.Message,
                                    Description = updateEx.StackTrace
                                };
                    throw new FaultException<ValidationFault>(fault);
            }
        }

		private static async Task<IEnumerable<Parts>> LoadRangeWhere<T>(int startIndex, int count,
            OCRContext dbContext, string exp, string navExp, string navProp, string rel, IEnumerable<string> includeLst = null) where T : class
        {
             switch (rel)
		    {
		        case "SelectMany":
                    return await LoadRangeSelectMany<T>(startIndex, count, dbContext, exp, navExp, navProp, includeLst)
									.ConfigureAwait(continueOnCapturedContext: false);
		            
                default:
                    return await LoadRangeSelect<T>(startIndex, count, dbContext, exp, navExp, navProp, includeLst)
									.ConfigureAwait(continueOnCapturedContext: false);
		    }
        }

		private static async Task<IEnumerable<Parts>> LoadRangeSelectMany<T>(int startIndex, int count,
            OCRContext dbContext, string exp, string navExp, string navProp, IEnumerable<string> includeLst = null) where T : class
        {
			try
			{
            var set = dbContext.Set<T>()
				.AsNoTracking()
                .Where(navExp)
                .SelectMany(navProp).OfType<Parts>();
    
            if (includeLst != null) set = includeLst.Aggregate(set, (current, itm) => current.Include(itm));            

            return set
                .Where(exp == "All" || exp == null ? "Id != null" : exp)
                .Distinct()
                .OrderBy(y => y.Id)
 
                .Skip(startIndex)
                .Take(count)
                .ToList();
			}
			catch (Exception)
			{
				Debugger.Break();
				throw;
			}
        }

