                .Where(navExp)
                .Select(navProp).OfType<Parts>();

               if (includeLst != null) set = includeLst.Aggregate(set, (current, itm) => current.Include(itm)); 
                
               return set
                .Where(exp == "All" || exp == null ? "Id != null" : exp)
                .Distinct()
                .OrderBy(y => y.Id)
 
                .Skip(startIndex)
                .Take(count)
                .ToList();
							 }
			catch (Exception)
			{
				Debugger.Break();
				throw;
			}
        }

