		public decimal SumField(string whereExp, string field)
         {
             try
             {
                 using (var dbContext = new OCRContext(){StartTracking = StartTracking})
                 {
                    dbContext.Database.CommandTimeout = 0;
					decimal res = 0;
                     if (string.IsNullOrEmpty(whereExp) || whereExp == "None") return 0;
                     if (whereExp == "All")
                     {
                          res = Convert.ToDecimal(dbContext.Parts.AsNoTracking().Sum(field));
                     }
                     else
                     {
                         res = Convert.ToDecimal(dbContext.Parts.AsNoTracking().Where(whereExp).Sum(field));
                     }
                     
                     return res;
                 }
             }
             catch (Exception updateEx)
             {
                System.Diagnostics.Debugger.Break();
                //throw new FaultException(updateEx.Message);
                    var fault = new ValidationFault
                                {
                                    Result = false,
                                    Message = updateEx.Message,
                                    Description = updateEx.StackTrace
                                };
                    throw new FaultException<ValidationFault>(fault);
             }
         }

        public async Task<decimal> SumNav( string exp, Dictionary<string, string> navExp, string field)
        {
            try
            {
                if (string.IsNullOrEmpty(exp) || exp == "None") return 0;
                using (var dbContext = new OCRContext(){StartTracking = StartTracking})
                {
                    dbContext.Database.CommandTimeout = 0;
                    if (!dbContext.Parts.Any()) return 0;
                    if (exp == "All" && navExp.Count == 0)
                    {
                        return Convert.ToDecimal(dbContext.Parts
										.AsNoTracking()
                                        .Sum(field)??0);
                    }
                    foreach (var itm in navExp)
                    {
                        switch (itm.Key)
                        {
                            case "End":
                                return await SumWhere<End>(dbContext, exp, itm.Value, "Parts", field, "Select")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "Templates":
                                return await SumWhere<Templates>(dbContext, exp, itm.Value, "Parts", field, "SelectMany")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "PartTypes":
                                return await SumWhere<PartTypes>(dbContext, exp, itm.Value, "Parts", field, "SelectMany")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "Start":
                                return await SumWhere<Start>(dbContext, exp, itm.Value, "Parts", field, "Select")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "Lines":
                                return await SumWhere<Lines>(dbContext, exp, itm.Value, "Parts", field, "Select")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "RecuringPart":
                                return await SumWhere<RecuringPart>(dbContext, exp, itm.Value, "Parts", field, "SelectMany")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "ChildParts":
                                return await SumWhere<ChildParts>(dbContext, exp, itm.Value, "ChildPart", field, "Select")
											.ConfigureAwait(continueOnCapturedContext: false);
                            case "ParentParts":
                                return await SumWhere<ChildParts>(dbContext, exp, itm.Value, "ChildPart", field, "Select")
											.ConfigureAwait(continueOnCapturedContext: false);
						}
                    }
                    return Convert.ToDecimal(dbContext.Parts.Where(exp == "All" || exp == null ? "Id != null" : exp)
											.AsNoTracking()
                                            .Sum(field)??0);
                }
                
            }
            catch (Exception updateEx)
            {
                System.Diagnostics.Debugger.Break();
                //throw new FaultException(updateEx.Message);
                var fault = new ValidationFault
                {
                    Result = false,
                    Message = updateEx.Message,
                    Description = updateEx.StackTrace
                };
                throw new FaultException<ValidationFault>(fault);
            }
        }

		private static async Task<decimal> SumWhere<T>(OCRContext dbContext, string exp, string navExp, string navProp, string field, string rel) where T : class
        {
              switch (rel)
		    {
		        case "SelectMany":
                    return await SumWhereSelectMany<T>(dbContext, exp, navExp, navProp, field)
									.ConfigureAwait(continueOnCapturedContext: false);
		        default:
                    return await SumWhereSelect<T>(dbContext, exp, navExp, navProp, field)
									.ConfigureAwait(continueOnCapturedContext: false);
		    }
        }

