		private static IQueryable<Parts> AddIncludes(IEnumerable<string> includesLst, OCRContext dbContext)
       {
		 try
			{
			   if (includesLst == null) includesLst = new List<string>();
			   var set =(DbQuery<Parts>) dbContext.Parts; 
			   set = includesLst.Where(x => !string.IsNullOrEmpty(x))
                                .Aggregate(set, (current, itm) => current.Include(itm));
			   return set;
			 }
			catch (Exception)
			{
				Debugger.Break();
				throw;
			}
       }
	   private IQueryable<Parts> AddWheres(List<string> expLst, IQueryable<Parts> set)
        {
            try
            {
                return expLst.Where(x => !string.IsNullOrEmpty(x))
                             .Aggregate(set, (current, itm) => current.Where(itm));
            }
            catch (Exception)
            {
                Debugger.Break();
                throw;
            }
          
        }

