        public async Task Send2Excel()
        {
			IEnumerable<AdjustmentEx> lst = null;
            using (var ctx = new AdjustmentExRepository())
            {
                lst = await ctx.GetAdjustmentExesByExpressionNav(vloader.FilterExpression, vloader.NavigationExpression).ConfigureAwait(continueOnCapturedContext: false);
            }
             if (lst == null || !lst.Any()) 
              {
                   MessageBox.Show("No Data to Send to Excel");
                   return;
               }
            var s = new ExportToCSV<AdjustmentExExcelLine, List<AdjustmentExExcelLine>>
            {
                dataToPrint = lst.Select(x => new AdjustmentExExcelLine
                {
 
                    InvoiceDate = x.InvoiceDate ,
                    
 
                    Type = x.Type ,
                    
 
                    DutyFreePaid = x.DutyFreePaid ,
                    
 
                    InvoiceNo = x.InvoiceNo ,
                    
 
                    ImportedLines = x.ImportedLines ,
                    
 
                    TotalLines = x.TotalLines ,
                    
 
                    Currency = x.Currency ,
                    
 
                    InvoiceTotal = x.InvoiceTotal ,
                    
 
                    ImportedTotal = x.ImportedTotal 
                    
                }).ToList()
            };
            using (var sta = new StaTaskScheduler(numberOfThreads: 1))
            {
               await Task.Factory.StartNew(s.GenerateReport, CancellationToken.None, TaskCreationOptions.None, sta).ConfigureAwait(false);
            }
        }

