        public async Task Send2Excel()
        {
			IEnumerable<InventoryItemsEx> lst = null;
            using (var ctx = new InventoryItemsExRepository())
            {
                lst = await ctx.GetInventoryItemsExByExpressionNav(vloader.FilterExpression, vloader.NavigationExpression).ConfigureAwait(continueOnCapturedContext: false);
            }
             if (lst == null || !lst.Any()) 
              {
                   MessageBox.Show("No Data to Send to Excel");
                   return;
               }
            var s = new ExportToCSV<InventoryItemsExExcelLine, List<InventoryItemsExExcelLine>>
            {
                dataToPrint = lst.Select(x => new InventoryItemsExExcelLine
                {
 
                    ItemNumber = x.ItemNumber ,
                    
 
                    Description = x.Description ,
                    
 
                    Category = x.Category ,
                    
 
                    TariffCode = x.TariffCode ,
                    
 
                    SuppUnitCode2 = x.SuppUnitCode2 ,
                    
 
                    SuppQty = x.SuppQty ,
                    
 
                    EntryTimeStamp = x.EntryTimeStamp 
                    
                }).ToList()
            };
            using (var sta = new StaTaskScheduler(numberOfThreads: 1))
            {
               await Task.Factory.StartNew(s.GenerateReport, CancellationToken.None, TaskCreationOptions.None, sta).ConfigureAwait(false);
            }
        }

